--Craição do DataBaseCREATE DATABASE ResiliaData;USE ResiliaData;--Criação tabelas--Criação da tabela empresaCREATE TABLE tb_empresa(id_empresa INT not null,nome_empresa VARCHAR (50),Endereco VARCHAR (50),CNPJ INT,CONSTRAINT pk_id_empresa PRIMARY KEY (id_empresa),);--Criação da tabela tecnologia CREATE TABLE tb_tecnologia( id_tecnologia int not null, nome_tecnologia varchar (50), area varchar (50), constraint pk_id_tecnologia primary key (id_tecnologia));--Criação da tabela empresa_tecnologiaCREATE TABLE tb_empresa_tecnologia(id_utiliza_emp_tec INT not null,id_empresa int,id_tecnologia int,CONSTRAINT pk_id_utiliza_emp_tec primary key (id_utiliza_emp_tec),CONSTRAINT fk_id_empresa FOREIGN KEY (id_empresa) REFERENCES tb_empresa,CONSTRAINT fk_id_tecnologia FOREIGN KEY (id_tecnologia) REFERENCES tb_tecnologia);--Inserindo dados na tabela empresainsert INTO tb_empresa VALUES(1,'Resilia','Rio de Janeiro','123456789'),(2,'Senac_Cursos','Nova Iguaçu','123456779');--Inserindo dados na tabela tecnologiainsert into tb_tecnologia VALUES(1,'python','dados'),(2,'sql','dados');select * from tb_empresa_tecnologiaselect * from tb_empresa